events.inlineCode:
if (global.ssangervasi) { return }

const within = (min, mid, max) => ( min <= mid && mid <= max)

// stupdily fancy :D
const charWithin = (chars, charCode) => {
    const min = chars[0]
    const max = chars[1]
    return within(min.charCodeAt(0), charCode, max.charCodeAt(max.length - 1))
}

const isBindable = (keyCode) =>  (
    // yr normy alnums
    charWithin`A ${keyCode} Z` ||
    // them wacky arrows
    charWithin`% ${keyCode} (` ||
    // just any dang fancy character 
    within(161, keyCode, 255)
)

const lastKeyCode = (runtimeScene) => {
    const game = runtimeScene.getGame()
    const inputManager = game.getInputManager()
    return inputManager.getLastPressedKey()
}

global.ssangervasi = {
    within,
    charWithin,
    isBindable,
    lastKeyCode
}

// console.log('bindable check', runtimeScene)
const lastKeyCode = global.ssangervasi.lastKeyCode(runtimeScene)
eventsFunctionContext.returnValue = global.ssangervasi.isBindable(lastKeyCode)
